import React, { useState, useEffect } from "react";

const Chat = () => {
    const [chatHistory, setChatHistory] = useState([]); // State to hold chat history
    const socket = new WebSocket("ws://localhost:5000");

    useEffect(() => {
        socket.onopen = () => {
            console.log("✅ Connected to WebSocket");
            socket.send("Hello, bot!"); // Send a test message
        };

        socket.onmessage = (event) => {
            const data = JSON.parse(event.data);

            if (data.history) {
                // Set chat history received from the server
                setChatHistory(data.history);
            } else if (data.message) {
                // Update chat history with the bot's response
                setChatHistory((prevHistory) => [
                    ...prevHistory,
                    { role: "assistant", content: data.message }
                ]);
            }
        };

        socket.onerror = (error) => console.error("❌ WebSocket Error:", error);
        socket.onclose = () => console.warn("⚠️ WebSocket Disconnected");

        // Clean up WebSocket connection when component unmounts
        return () => {
            socket.close();
        };
    }, []);

    // Render chat history and input field
    return (
        <div>
            <div className="chat" style={{ maxHeight: "400px", overflowY: "scroll", border: "1px solid #ccc", padding: "10px" }}>
                {chatHistory.map((msg, index) => (
                    <div key={index} style={{ marginBottom: "10px" }}>
                        <strong>{msg.role === "user" ? "You" : "Bot"}:</strong> {msg.content}
                    </div>
                ))}
            </div>

            <input
                type="text"
                onKeyDown={(e) => {
                    if (e.key === "Enter") {
                        const userMessage = e.target.value;
                        socket.send(userMessage); // Send message to the WebSocket server
                        setChatHistory((prevHistory) => [
                            ...prevHistory,
                            { role: "user", content: userMessage }
                        ]); // Add user message to chat history
                        e.target.value = ""; // Clear input field
                    }
                }}
                style={{ marginTop: "10px", padding: "8px", width: "100%" }}
            />
        </div>
    );
};

export default Chat;
