import React, { useState } from "react";
import useWebSocket from "react-use-websocket";

const SOCKET_URL = "ws://localhost:5000"; // WebSocket backend URL

const Chat = () => {
    const [message, setMessage] = useState("");
    const [chat, setChat] = useState([]);

    const { sendMessage, lastMessage } = useWebSocket(SOCKET_URL, {
        onOpen: () => console.log("Connected to WebSocket"),
        onMessage: (event) => {
            setChat((prevChat) => [...prevChat, { sender: "bot", text: event.data }]);
        },
    });

    const handleSendMessage = () => {
        if (message.trim()) {
            setChat((prevChat) => [...prevChat, { sender: "user", text: message }]);
            sendMessage(message);
            setMessage("");
        }
    };

    return (
        <div style={{ maxWidth: "500px", margin: "auto", textAlign: "center" }}>
            <h2>WebSocket Chat</h2>
            <div style={{ border: "1px solid #ddd", padding: "10px", height: "300px", overflowY: "scroll" }}>
                {chat.map((msg, index) => (
                    <div key={index} style={{ textAlign: msg.sender === "user" ? "right" : "left" }}>
                        <strong>{msg.sender}:</strong> {msg.text}
                    </div>
                ))}
            </div>
            <input 
                type="text" 
                value={message} 
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type a message..." 
            />
            <button onClick={handleSendMessage}>Send</button>
        </div>
    );
};

export default Chat;
